const baseURL = "http://localhost:4000/swipe?direction=";
import React from 'react';
import axios from 'axios';
import "./buttons.css";

class SwipeButton extends React.Component {

    constructor(props){
        super(props);
        this.state = {
        };
        this.click = this.click.bind(this);
    }
    onTrigger = (data) => {
        this.props.parentCallback(data);
    }

    click(direction) {
        this.setState({ isLoading: true });

        axios.get(baseURL+direction, )
            .then((response) => {
                this.onTrigger(response.data)
                console.log(this.props.direction)
            })
            .catch((err) => {
                console.log(err);
            });
    }

    render() {
        const {direction} = this.props;
        return  (
            <div>
                <div><button className="swipeBtn" onClick={() => this.click({direction})} > {direction} </button></div>
            </div>
        );
    }
}

export default SwipeButton;
