const baseURL = "http://localhost:4000/swipe?direction=";
import React from 'react';
import axios from 'axios';
import "./swipeBtn.css";

class ButtonComponent extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            isLoading: false,
        };
        this.click = this.click.bind(this);
    }
    onTrigger = (data) => {
        this.props.parentCallback(data);
    }

    click(direction) {
        this.setState({ isLoading: true });

        axios.get(baseURL+direction, )
            .then((response) => {
                this.onTrigger(response.data)
            })
            .catch((err) => {
                console.log(err);
            });
    }

    render() {
        return  (
            <div>
                <button className="swipeBtn" onClick={() => this.click("next")} > next &laquo; </button>
                <button className="swipeBtn" onClick={() => this.click("previous")} > &laquo; previous </button>
            </div>
        );
    }
}

export default ButtonComponent;
