import React, { Component } from 'react';
import ButtonComponent from "./swipeBtn";
import axios from "axios";


class ImageComponent extends Component {
    constructor(props){
        super(props);
        this.baseUrl = "http://localhost:4000/load?id="
    }

    state = {
        name: "",
    }
    componentDidMount() {
        console.log("componentDidMount")
        axios.get("http://localhost:4000/swipe?direction=", )
            .then((response) => {
                this.setState({name: this.baseUrl + response})
            })
            .catch((err) => {
                console.log(err);
            });
    }
    handleCallback = (childData) =>{
        console.log(childData)
        this.setState({name: this.baseUrl + childData})
    }

    render() {
        const {name} = this.state;
        return(

            <div>
                <ButtonComponent parentCallback = {this.handleCallback} />
                <React.Fragment>
                    <img src={name} alt="image" />
                </React.Fragment>
            </div>
        )
    }
}

export default ImageComponent;


