import React, { Component } from 'react';
import ButtonComponent from "./customBtn";



class ImageComponent extends Component {
    constructor(props){
        super(props);
        this.basUrl = "http://localhost:4000/load?id="
    }

    state = {
        name: "",
    }

    handleCallback = (childData) =>{
        var lalka = this.basUrl + "1"
        this.setState({name: childData});
        this.setState({baseUrl: lalka})
    }

    render() {
        return(

            <div>
                <ButtonComponent parentCallback = {this.handleCallback} />
                <React.Fragment>
                    <img src={this.baseUrl} alt="image" />
                    <p>{this.basUrl}</p>
                    <p>{name}</p>
                </React.Fragment>
            </div>
        )
    }
}

export default ImageComponent;


